import "./Courses.css";
import { useEffect, setWorkouts, useState } from "react";
const Courses = (props) => {
  const [Workouts, setWorkouts]= useState(null)

  useEffect(()=> {
    const fetchWorouts = async () => {
      const response = await fetch('/api/courses')
      const json = await response.json()

      if (response.ok){
        setWorkouts(json)
      }
    }
    fetchWorouts() 
  }, [])

  return (
    <div className="courses" meta name="viewport" content="width=device-width, initial-scale=1.0">

<p>{props.showCompleted? "completed courses":""}</p>
<p>{props.showInprogress?"Inprogrss coursdes":""}</p>
<div class="flex-container">
 
{Workouts && Workouts.map((workouts) =>(
          <span className= "courses-box" key={workouts.id} > 
        
          <img  className="img" src={workouts.img} /> <p className="title">{workouts.title}</p> <p>{workouts.description} </p>

          <div className="progress">
            <div className="progress-done">
            width{workouts.percentage}
            </div>
            </div>


           </span>
          
          

        ))}
       
        </div>
       
             
        
     
    </div>
  );
};

export default Courses;
